<?phpif (!$is_included) exit();$geoip_loaded = false;$geoip_pointer = NULL;function load_geoip() {	global $geoip_loaded, $geoip_pointer, $config;		if ($geoip_loaded) return;		include "api/geoip/geoip.php";		if (!file_exists($config['geoip_db_path'])) {		die("GeoIP Database not found.");	}	$geoip_pointer = geoip_open($config['geoip_db_path'], GEOIP_STANDARD);	$geoip_loaded = $geoip_pointer != NULL;}function get_country_name($addr) {	global $geoip_pointer;	load_geoip();		$country = geoip_country_name_by_addr($geoip_pointer, $addr);		return $country == "" ? "Unknown" : $country;}function close_geoip() {	global $geoip_loaded, $geoip_pointer;	if ($geoip_loaded) geoip_close($geoip_pointer);}?>